import { app, BrowserWindow, Tray, Menu, nativeImage } from 'electron';

// This allows TypeScript to pick up the magic constant that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  // eslint-disable-line global-require
  app.quit();
}

let mainWindow: BrowserWindow;

const createWindow = (): void => {
  // Create the browser window.
  mainWindow = new BrowserWindow({
    height: 600 * 1.2,
    width: 800 * 1.2,
    webPreferences: {
      devTools: true
    }
  });

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  const { session: { webRequest } } = mainWindow.webContents;
  const filter = {
    urls: [
      'http://localhost:33333/keycloak-redirect*'
    ]
  };
  webRequest.onBeforeRequest(filter, async ({ url }) => {
    console.log(url);
    const params = url.slice(url.indexOf('#'));
    console.log(params);
    console.log(MAIN_WINDOW_WEBPACK_ENTRY + params);
    mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY + params);
  });

  // Open the DevTools.
  // mainWindow.webContents.openDevTools();
};

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', createWindow);

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

// In this file you can include the rest of your app's specific main process
// code. You can also put them in separate files and import them here.

let tray;

app.whenReady().then(() => {
  const icon = nativeImage.createFromDataURL(
    "data:image/png;base64,"
  );
  tray = new Tray(icon);

  tray.on("click", () => {
    if (mainWindow.isVisible()) {
      mainWindow.hide();
    } else {
      mainWindow.show();
    }
  });

  const contextMenu = Menu.buildFromTemplate([
    {
      label: "Minimize to tray",
      click: async () => {
        mainWindow.hide();
      },
    },
    { type: "separator" },
    {
      label: "Force Reload",
      click: async () => {
        mainWindow.webContents.reloadIgnoringCache();
      },
    },
    {
      label: "Help",
      click: async () => {
        const { shell } = require("electron");
        await shell.openExternal(
          "https://github.com/Sheherezadhe/awair-uploader"
        );
      },
    },
    {
      label: "Version " + app.getVersion(),
    },
    { type: "separator" },
    {
      label: "Quit",
      click: async () => {
        mainWindow.destroy();
        app.quit();
      },
    },
  ]);

  tray.setToolTip("Awair Uploader.");
  tray.setContextMenu(contextMenu);
});
